        [CorrectnessTest]
        public void Add() { Assert.AreEqual( v1 + v2, new Vector3d(4, 1.0, 1.0)); }
        [CorrectnessTest]
        public void Sub() { Assert.AreEqual( v1 - v2, new Vector3d(-2, 3, 5)); }
        [CorrectnessTest]
        public void Dot() { Assert.AreEqual( v1 * v2, -5); }
        [CorrectnessTest]
        public void Cross() { Assert.AreEqual((v5 ^ v6), new Vector3d(0.0, 0.0, 1.0)); }
        //#ifdef Sqrt

        [CorrectnessTest]
        public void Length() { Assert.AreEqual(v3.Length, 5); }
        [CorrectnessTest]
        public void DirectionNormal() { Assert.AreEqual(v1.Normal, v1.Direction); }
        //#endif
        [CorrectnessTest]
        public void Length2() { Assert.AreEqual(v3.Length2, 25); }
        [CorrectnessTest]
        public void SwizzleXXX() {  Assert.AreEqual(v1.XXX, new Vector3d(1.0, 1.0, 1.0)); }
        [CorrectnessTest]
        public void SwizzleXZY() {  Assert.AreEqual(v1.XZY, new Vector3d(1.0, 3, 2)); }
        [CorrectnessTest]
        public void WSwizzleXXX()
        {
            Vector3d v4 = v1;
            v4.XXX = new Vector3d(10.0, 2, 1.0);
            Assert.AreEqual(v1, v4);
        }
    }
